import React, { useState, useRef, useEffect } from 'react';
import ReactModal from 'react-modal';
import ReactCrop from 'react-image-crop';
import 'react-image-crop/dist/ReactCrop.css';
import './ImageModal.css';

// Set the app element for react-modal
if (typeof window !== 'undefined') {
  ReactModal.setAppElement('#root');
}

const ImageModal = ({ image, isOpen, onClose, onSave }) => {
  const [activeTool, setActiveTool] = useState('view');
  const [brushColor, setBrushColor] = useState('#ff0000');
  const [brushWidth, setBrushWidth] = useState(5);
  const [isDrawing, setIsDrawing] = useState(false);
  const [imageLoaded, setImageLoaded] = useState(false);
  const [cropMode, setCropMode] = useState(false);
  const [crop, setCrop] = useState({ unit: 'px', width: 200, height: 200, x: 0, y: 0 });
  const [completedCrop, setCompletedCrop] = useState(null);
  
  const canvasRef = useRef(null);
  const contextRef = useRef(null);
  const imgRef = useRef(null);
  const originalImageRef = useRef(null);

  // Initialize when modal opens
  useEffect(() => {
    if (isOpen && image) {
      setImageLoaded(false);
      setCropMode(false);
      loadImageToCanvas();
    }
  }, [isOpen, image]);

  const loadImageToCanvas = () => {
    if (!canvasRef.current) return;

    const img = new Image();
    img.crossOrigin = 'anonymous';
    img.onload = () => {
      originalImageRef.current = img;
      const canvas = canvasRef.current;
      const ctx = canvas.getContext('2d');
      contextRef.current = ctx;
      
      // Set canvas dimensions to match image
      canvas.width = img.width;
      canvas.height = img.height;
      
      // Draw image on canvas
      ctx.drawImage(img, 0, 0);
      setImageLoaded(true);
    };
    img.onerror = () => {
      console.error('Failed to load image');
      setImageLoaded(false);
    };
    img.src = image.file_path;
  };

  // Drawing functions
  const startDrawing = (e) => {
    if (activeTool !== 'brush' || !contextRef.current) return;
    
    const canvas = canvasRef.current;
    const rect = canvas.getBoundingClientRect();
    const scaleX = canvas.width / rect.width;
    const scaleY = canvas.height / rect.height;
    const x = (e.clientX - rect.left) * scaleX;
    const y = (e.clientY - rect.top) * scaleY;
    
    contextRef.current.beginPath();
    contextRef.current.moveTo(x, y);
    contextRef.current.lineWidth = brushWidth;
    contextRef.current.strokeStyle = brushColor;
    contextRef.current.lineCap = 'round';
    setIsDrawing(true);
  };

  const draw = (e) => {
    if (!isDrawing || !contextRef.current) return;
    
    const canvas = canvasRef.current;
    const rect = canvas.getBoundingClientRect();
    const scaleX = canvas.width / rect.width;
    const scaleY = canvas.height / rect.height;
    const x = (e.clientX - rect.left) * scaleX;
    const y = (e.clientY - rect.top) * scaleY;
    
    contextRef.current.lineTo(x, y);
    contextRef.current.stroke();
  };

  const stopDrawing = () => {
    if (contextRef.current) {
      contextRef.current.closePath();
    }
    setIsDrawing(false);
  };

  const handleAddText = () => {
    if (!contextRef.current) return;
    
    const text = prompt('Enter text to add to the image:');
    if (text) {
      contextRef.current.font = '24px Arial';
      contextRef.current.fillStyle = brushColor;
      contextRef.current.fillText(text, 50, 50);
    }
  };

  const handleRotate = (degrees) => {
    if (!contextRef.current || !canvasRef.current || !originalImageRef.current) return;
    
    const canvas = canvasRef.current;
    const ctx = contextRef.current;
    const tempCanvas = document.createElement('canvas');
    const tempCtx = tempCanvas.getContext('2d');
    
    // Set temp canvas size (swap dimensions for 90Â° rotations)
    if (Math.abs(degrees) === 90) {
      tempCanvas.width = canvas.height;
      tempCanvas.height = canvas.width;
    } else {
      tempCanvas.width = canvas.width;
      tempCanvas.height = canvas.height;
    }
    
    // Rotate and draw
    tempCtx.translate(tempCanvas.width / 2, tempCanvas.height / 2);
    tempCtx.rotate((degrees * Math.PI) / 180);
    tempCtx.drawImage(canvas, -canvas.width / 2, -canvas.height / 2);
    
    // Resize main canvas and draw rotated image
    canvas.width = tempCanvas.width;
    canvas.height = tempCanvas.height;
    ctx.drawImage(tempCanvas, 0, 0);
  };

  const handleCropComplete = async () => {
    if (!imgRef.current || !completedCrop || !contextRef.current || !canvasRef.current) return;

    try {
      const croppedImage = await getCroppedImg(imgRef.current, completedCrop);
      const img = new Image();
      img.onload = () => {
        const canvas = canvasRef.current;
        const ctx = contextRef.current;
        canvas.width = img.width;
        canvas.height = img.height;
        ctx.drawImage(img, 0, 0);
        setCropMode(false);
        originalImageRef.current = img;
      };
      img.src = croppedImage;
    } catch (error) {
      console.error('Error cropping image:', error);
      alert('Error cropping image. Please try again.');
    }
  };

  const getCroppedImg = (image, crop) => {
    const canvas = document.createElement('canvas');
    const ctx = canvas.getContext('2d');
    const scaleX = image.naturalWidth / image.width;
    const scaleY = image.naturalHeight / image.height;

    canvas.width = crop.width;
    canvas.height = crop.height;

    ctx.drawImage(
      image,
      crop.x * scaleX,
      crop.y * scaleY,
      crop.width * scaleX,
      crop.height * scaleY,
      0,
      0,
      crop.width,
      crop.height
    );

    return canvas.toDataURL('image/png');
  };

  const handleResize = () => {
    const newWidth = prompt('Enter new width (in pixels):');
    const newHeight = prompt('Enter new height (in pixels):');
    
    if (newWidth && newHeight && !isNaN(newWidth) && !isNaN(newHeight)) {
      if (!contextRef.current || !canvasRef.current || !originalImageRef.current) return;
      
      const canvas = canvasRef.current;
      const ctx = contextRef.current;
      const tempCanvas = document.createElement('canvas');
      const tempCtx = tempCanvas.getContext('2d');
      
      tempCanvas.width = parseInt(newWidth);
      tempCanvas.height = parseInt(newHeight);
      
      // Draw original image resized
      tempCtx.drawImage(originalImageRef.current, 0, 0, tempCanvas.width, tempCanvas.height);
      
      // Update main canvas
      canvas.width = tempCanvas.width;
      canvas.height = tempCanvas.height;
      ctx.drawImage(tempCanvas, 0, 0);
    }
  };

  const handleClear = () => {
    if (!contextRef.current || !canvasRef.current || !originalImageRef.current) return;
    
    const canvas = canvasRef.current;
    const ctx = contextRef.current;
    const img = originalImageRef.current;
    
    canvas.width = img.width;
    canvas.height = img.height;
    ctx.drawImage(img, 0, 0);
  };

  const handleSave = () => {
    if (!canvasRef.current) return;
    
    try {
      const dataURL = canvasRef.current.toDataURL('image/png');
      onSave({
        imageData: dataURL,
        filename: image.filename
      });
    } catch (error) {
      console.error('Error saving image:', error);
      alert('Error saving image. Please try again.');
    }
  };

  return (
    <ReactModal
      isOpen={isOpen}
      onRequestClose={onClose}
      className="image-modal"
      overlayClassName="image-modal-overlay"
      shouldCloseOnOverlayClick={false}
    >
      <div className="modal-header">
        <h3>Edit Image: {image?.title || image?.original_filename}</h3>
        <button onClick={onClose} className="close-btn">Ã</button>
      </div>

      <div className="modal-content">
        <div className="toolbar">
          <div className="tool-group">
            <button
              className={activeTool === 'view' ? 'active' : ''}
              onClick={() => setActiveTool('view')}
            >
              ðï¸ View
            </button>
            <button
              className={activeTool === 'brush' ? 'active' : ''}
              onClick={() => setActiveTool('brush')}
            >
              ðï¸ Draw
            </button>
            <button onClick={handleAddText}>
              ð Text
            </button>
            <button onClick={() => handleRotate(90)}>
              ð 90Â°
            </button>
            <button onClick={() => handleRotate(-90)}>
              ð -90Â°
            </button>
            <button onClick={() => setCropMode(!cropMode)}>
              âï¸ Crop
            </button>
            <button onClick={handleResize}>
              ð Resize
            </button>
            <button onClick={handleClear}>
              ðï¸ Reset
            </button>
          </div>

          {activeTool === 'brush' && (
            <div className="brush-controls">
              <label>Color:</label>
              <input
                type="color"
                value={brushColor}
                onChange={(e) => setBrushColor(e.target.value)}
              />
              <label>Size:</label>
              <input
                type="range"
                min="1"
                max="20"
                value={brushWidth}
                onChange={(e) => setBrushWidth(parseInt(e.target.value))}
              />
              <span>{brushWidth}px</span>
            </div>
          )}
        </div>

        <div className="canvas-container">
          {!imageLoaded ? (
            <div className="loading">Loading image...</div>
          ) : cropMode ? (
            <div className="crop-mode">
              <ReactCrop
                crop={crop}
                onChange={setCrop}
                onComplete={setCompletedCrop}
                keepSelection={true}
              >
                <img
                  ref={imgRef}
                  src={image.file_path}
                  alt="Crop preview"
                  onLoad={() => setCrop({ unit: 'px', width: 200, height: 200, x: 0, y: 0 })}
                  style={{ maxWidth: '100%', maxHeight: '60vh' }}
                />
              </ReactCrop>
              <div className="crop-controls">
                <button onClick={handleCropComplete} className="btn btn-primary">
                  Apply Crop
                </button>
                <button onClick={() => setCropMode(false)} className="btn btn-secondary">
                  Cancel
                </button>
              </div>
            </div>
          ) : (
            <canvas
              ref={canvasRef}
              className="drawing-canvas"
              onMouseDown={activeTool === 'brush' ? startDrawing : undefined}
              onMouseMove={activeTool === 'brush' ? draw : undefined}
              onMouseUp={activeTool === 'brush' ? stopDrawing : undefined}
              onMouseLeave={activeTool === 'brush' ? stopDrawing : undefined}
              style={{ 
                cursor: activeTool === 'brush' ? 'crosshair' : 'default',
                maxWidth: '100%',
                maxHeight: '60vh',
                border: '2px solid var(--accent)',
                borderRadius: '8px'
              }}
            />
          )}
        </div>
      </div>

      <div className="modal-footer">
        <div className="image-info">
          {image && canvasRef.current && (
            <>
              <span>Size: {canvasRef.current.width}Ã{canvasRef.current.height}</span>
              <span>Original: {image.width}Ã{image.height}</span>
            </>
          )}
        </div>
        <div className="modal-actions">
          <button onClick={onClose} className="btn btn-secondary">
            Cancel
          </button>
          <button onClick={handleSave} className="btn btn-primary">
            Save Changes
          </button>
        </div>
      </div>
    </ReactModal>
  );
};

export default ImageModal;
