import React, { useState, useRef, useEffect } from 'react';
import ReactModal from 'react-modal';
import ReactCrop from 'react-image-crop';
import 'react-image-crop/dist/ReactCrop.css';
import './ImageModal.css';

// Set the app element for react-modal
if (typeof window !== 'undefined') {
  ReactModal.setAppElement('#root');
}

const ImageModal = ({ image, isOpen, onClose, onSave }) => {
  const [imageLoaded, setImageLoaded] = useState(false);
  const [cropMode, setCropMode] = useState(false);
  const [crop, setCrop] = useState({ unit: 'px', width: 100, height: 100, x: 0, y: 0 });
  const [completedCrop, setCompletedCrop] = useState(null);
  const [rotation, setRotation] = useState(0);
  const [scale, setScale] = useState(1);
  
  const imgRef = useRef(null);

  useEffect(() => {
    if (isOpen && image) {
      setImageLoaded(false);
      setCropMode(false);
      setRotation(0);
      setScale(1);
      // Initialize crop with proper defaults
      setCrop({ unit: 'px', width: 100, height: 100, x: 0, y: 0 });
      setCompletedCrop(null);
    }
  }, [isOpen, image]);

  const handleImageLoad = (e) => {
    setImageLoaded(true);
    
    // Set initial crop based on image size when it loads
    if (e.target && e.target.naturalWidth) {
      const initialCropSize = Math.min(e.target.naturalWidth, e.target.naturalHeight) * 0.5;
      setCrop({
        unit: 'px',
        width: initialCropSize,
        height: initialCropSize,
        x: (e.target.naturalWidth - initialCropSize) / 2,
        y: (e.target.naturalHeight - initialCropSize) / 2
      });
    }
  };

  const handleImageError = () => {
    console.error('Failed to load image');
    setImageLoaded(false);
  };

  const handleRotate = (degrees) => {
    setRotation(prev => (prev + degrees) % 360);
  };

  const handleResize = () => {
    const newScale = prompt('Enter scale factor (0.1 to 5):', scale);
    if (newScale && !isNaN(newScale)) {
      const scaleValue = parseFloat(newScale);
      if (scaleValue >= 0.1 && scaleValue <= 5) {
        setScale(scaleValue);
      } else {
        alert('Scale must be between 0.1 and 5');
      }
    }
  };

  // Function to fetch image with CORS headers
  const fetchImageWithCORS = async (url) => {
    try {
      const response = await fetch(url, {
        mode: 'cors',
        credentials: 'omit'
      });
      
      if (!response.ok) {
        throw new Error('Failed to fetch image');
      }
      
      const blob = await response.blob();
      return URL.createObjectURL(blob);
    } catch (error) {
      console.error('Error fetching image:', error);
      return url;
    }
  };

  const getCroppedImg = async (imageElement, crop) => {
    if (!crop || !imageElement) {
      console.error('Crop or image element is missing');
      return null;
    }

    try {
      // Ensure crop has all required properties with defaults
      const safeCrop = {
        unit: crop.unit || 'px',
        x: crop.x || 0,
        y: crop.y || 0,
        width: crop.width || 100,
        height: crop.height || 100
      };

      const corsImageUrl = await fetchImageWithCORS(imageElement.src);
      
      return new Promise((resolve) => {
        const image = new Image();
        image.crossOrigin = 'anonymous';
        
        image.onload = () => {
          try {
            const canvas = document.createElement('canvas');
            const ctx = canvas.getContext('2d');
            
            let cropX, cropY, cropWidth, cropHeight;
            
            if (safeCrop.unit === '%') {
              cropX = (safeCrop.x / 100) * image.naturalWidth;
              cropY = (safeCrop.y / 100) * image.naturalHeight;
              cropWidth = (safeCrop.width / 100) * image.naturalWidth;
              cropHeight = (safeCrop.height / 100) * image.naturalHeight;
            } else {
              cropX = safeCrop.x;
              cropY = safeCrop.y;
              cropWidth = safeCrop.width;
              cropHeight = safeCrop.height;
            }

            // Ensure crop coordinates are within image bounds
            cropX = Math.max(0, Math.min(cropX, image.naturalWidth));
            cropY = Math.max(0, Math.min(cropY, image.naturalHeight));
            cropWidth = Math.max(1, Math.min(cropWidth, image.naturalWidth - cropX));
            cropHeight = Math.max(1, Math.min(cropHeight, image.naturalHeight - cropY));

            canvas.width = cropWidth;
            canvas.height = cropHeight;

            ctx.drawImage(
              image,
              cropX,
              cropY,
              cropWidth,
              cropHeight,
              0,
              0,
              cropWidth,
              cropHeight
            );

            resolve(canvas.toDataURL('image/png'));
          } catch (error) {
            console.error('Error in canvas drawing:', error);
            resolve(null);
          }
        };
        
        image.onerror = () => {
          console.error('Failed to load image for cropping');
          resolve(null);
        };
        
        image.src = corsImageUrl;
      });
    } catch (error) {
      console.error('Error in getCroppedImg:', error);
      return null;
    }
  };

  const handleCropComplete = async () => {
    if (!imgRef.current || !completedCrop) {
      alert('Please select a crop area first');
      return;
    }

    try {
      const croppedImage = await getCroppedImg(imgRef.current, completedCrop);
      
      if (croppedImage) {
        // Update the image source with the cropped version
        imgRef.current.src = croppedImage;
        setCropMode(false);
        setRotation(0);
        setScale(1);
        setCompletedCrop(null);
      } else {
        alert('Crop failed. Please try again.');
      }
    } catch (error) {
      console.error('Error cropping image:', error);
      alert('Error cropping image. Please try again.');
    }
  };

  const handleSave = async () => {
    if (!imgRef.current) return;
    
    try {
      const corsImageUrl = await fetchImageWithCORS(imgRef.current.src);
      
      const img = new Image();
      img.crossOrigin = 'anonymous';
      
      img.onload = () => {
        try {
          const canvas = document.createElement('canvas');
          const ctx = canvas.getContext('2d');
          
          // Set canvas dimensions
          canvas.width = img.naturalWidth;
          canvas.height = img.naturalHeight;
          
          // Draw the current image
          ctx.drawImage(img, 0, 0, canvas.width, canvas.height);
          
          const dataURL = canvas.toDataURL('image/png');
          onSave({
            imageData: dataURL,
            filename: image.filename
          });
        } catch (error) {
          console.error('Error in canvas operations:', error);
          alert('Error saving image.');
        }
      };
      
      img.onerror = () => {
        alert('Error loading image for saving.');
      };
      
      img.src = corsImageUrl;
    } catch (error) {
      console.error('Error saving image:', error);
      alert('Error saving image. Please try again.');
    }
  };

  const resetTransformations = () => {
    setRotation(0);
    setScale(1);
    if (image) {
      imgRef.current.src = image.file_path;
    }
  };

  const onCropChange = (newCrop) => {
    // Ensure crop has all required properties
    const safeCrop = {
      unit: newCrop.unit || 'px',
      x: newCrop.x || 0,
      y: newCrop.y || 0,
      width: newCrop.width || 100,
      height: newCrop.height || 100
    };
    setCrop(safeCrop);
  };

  const onCropComplete = (crop) => {
    // Ensure completed crop has all required properties
    const safeCrop = {
      unit: crop.unit || 'px',
      x: crop.x || 0,
      y: crop.y || 0,
      width: crop.width || 100,
      height: crop.height || 100
    };
    setCompletedCrop(safeCrop);
  };

  return (
    <ReactModal
      isOpen={isOpen}
      onRequestClose={onClose}
      className="image-modal"
      overlayClassName="image-modal-overlay"
      shouldCloseOnOverlayClick={false}
    >
      <div className="modal-header">
        <h3>Edit Image: {image?.title || image?.original_filename}</h3>
        <button onClick={onClose} className="close-btn">×</button>
      </div>

      <div className="modal-content">
        <div className="toolbar">
          <div className="tool-group">
            <button onClick={() => handleRotate(90)} className="btn">
              🔄 Rotate 90°
            </button>
            <button onClick={() => handleRotate(-90)} className="btn">
              🔄 Rotate -90°
            </button>
            <button onClick={handleResize} className="btn">
              📏 Resize
            </button>
            <button 
              onClick={() => setCropMode(!cropMode)} 
              className={cropMode ? 'btn active' : 'btn'}
            >
              ✂️ {cropMode ? 'Cancel Crop' : 'Crop'}
            </button>
            <button onClick={resetTransformations} className="btn">
              🔄 Reset
            </button>
          </div>

          {cropMode && completedCrop && (
            <div className="crop-controls">
              <button onClick={handleCropComplete} className="btn btn-primary">
                Apply Crop
              </button>
            </div>
          )}
        </div>

        <div className="canvas-container">
          {!imageLoaded && (
            <div className="loading">Loading image...</div>
          )}
          
          {cropMode ? (
            <ReactCrop
              crop={crop}
              onChange={onCropChange}
              onComplete={onCropComplete}
              keepSelection={true}
              ruleOfThirds
            >
              <img
                ref={imgRef}
                src={image?.file_path}
                alt={image?.alt_text || image?.title || image?.original_filename}
                onLoad={handleImageLoad}
                onError={handleImageError}
                crossOrigin="anonymous"
                style={{
                  maxWidth: '100%',
                  maxHeight: '60vh',
                  display: imageLoaded ? 'block' : 'none',
                  margin: '0 auto',
                  transform: `rotate(${rotation}deg) scale(${scale})`,
                  transformOrigin: 'center'
                }}
              />
            </ReactCrop>
          ) : (
            <img
              ref={imgRef}
              src={image?.file_path}
              alt={image?.alt_text || image?.title || image?.original_filename}
              onLoad={handleImageLoad}
              onError={handleImageError}
              crossOrigin="anonymous"
              style={{
                maxWidth: '100%',
                maxHeight: '60vh',
                display: imageLoaded ? 'block' : 'none',
                margin: '0 auto',
                transform: `rotate(${rotation}deg) scale(${scale})`,
                transformOrigin: 'center'
              }}
            />
          )}
        </div>
      </div>

      <div className="modal-footer">
        <div className="image-info">
          {image && (
            <>
              <span>Original: {image.width}×{image.height}</span>
              <span>Rotation: {rotation}°</span>
              <span>Scale: {scale}x</span>
              {completedCrop && cropMode && (
                <span>
                  Crop: {Math.round(completedCrop.width)}×{Math.round(completedCrop.height)}
                </span>
              )}
            </>
          )}
        </div>
        <div className="modal-actions">
          <button onClick={onClose} className="btn btn-secondary">
            Cancel
          </button>
          <button onClick={handleSave} className="btn btn-primary">
            Save Changes
          </button>
        </div>
      </div>
    </ReactModal>
  );
};

export default ImageModal;
