import React, { useState, useRef } from 'react';
import './ImageCropper.css';

const ImageCropper = ({ imageSrc, onCropComplete, onCancel }) => {
  const [isCropping, setIsCropping] = useState(false);
  const [cropStart, setCropStart] = useState({ x: 0, y: 0 });
  const [cropEnd, setCropEnd] = useState({ x: 0, y: 0 });
  const [isDragging, setIsDragging] = useState(false);
  const imgRef = useRef(null);

  const handleMouseDown = (e) => {
    if (!imgRef.current) return;
    
    const rect = imgRef.current.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;
    
    setCropStart({ x, y });
    setCropEnd({ x, y });
    setIsDragging(true);
    setIsCropping(true);
  };

  const handleMouseMove = (e) => {
    if (!isDragging || !imgRef.current) return;
    
    const rect = imgRef.current.getBoundingClientRect();
    const x = Math.max(0, Math.min(e.clientX - rect.left, rect.width));
    const y = Math.max(0, Math.min(e.clientY - rect.top, rect.height));
    
    setCropEnd({ x, y });
  };

  const handleMouseUp = () => {
    if (!isDragging) return;
    setIsDragging(false);
  };

  const handleCrop = () => {
    if (!imgRef.current) return;

    const canvas = document.createElement('canvas');
    const ctx = canvas.getContext('2d');
    
    const width = Math.abs(cropEnd.x - cropStart.x);
    const height = Math.abs(cropEnd.y - cropStart.y);
    
    if (width === 0 || height === 0) {
      alert('Please select a valid crop area');
      return;
    }

    canvas.width = width;
    canvas.height = height;

    ctx.drawImage(
      imgRef.current,
      Math.min(cropStart.x, cropEnd.x),
      Math.min(cropStart.y, cropEnd.y),
      width,
      height,
      0,
      0,
      width,
      height
    );

    canvas.toBlob((blob) => {
      if (blob) {
        onCropComplete(blob);
      }
    }, 'image/jpeg', 0.9);
  };

  const getCropStyle = () => {
    if (!isCropping) return { display: 'none' };
    
    const left = Math.min(cropStart.x, cropEnd.x);
    const top = Math.min(cropStart.y, cropEnd.y);
    const width = Math.abs(cropEnd.x - cropStart.x);
    const height = Math.abs(cropEnd.y - cropStart.y);

    return {
      left: `${left}px`,
      top: `${top}px`,
      width: `${width}px`,
      height: `${height}px`,
      border: '2px dashed #bb86fc',
      backgroundColor: 'rgba(187, 134, 252, 0.2)',
      position: 'absolute',
      pointerEvents: 'none'
    };
  };

  return (
    <div className="cropper-modal">
      <div className="cropper-content">
        <h3>Crop Image</h3>
        <div className="cropper-container">
          <div 
            className="image-wrapper"
            onMouseDown={handleMouseDown}
            onMouseMove={handleMouseMove}
            onMouseUp={handleMouseUp}
            onMouseLeave={handleMouseUp}
          >
            <img
              ref={imgRef}
              src={imageSrc}
              alt="Crop preview"
              style={{ maxWidth: '100%', maxHeight: '400px', cursor: 'crosshair' }}
            />
            <div style={getCropStyle()} />
          </div>
        </div>
        <div className="cropper-controls">
          <button onClick={onCancel} className="btn">
            Cancel
          </button>
          <button 
            onClick={handleCrop} 
            className="btn btn-primary"
            disabled={!isCropping}
          >
            Apply Crop
          </button>
        </div>
      </div>
    </div>
  );
};

export default ImageCropper;
