import React, { useState, useRef, useEffect } from 'react';
import ReactModal from 'react-modal';
import { FabricJSCanvas, useFabricJSEditor } from 'fabricjs-react';
import ReactCrop from 'react-image-crop';
import 'react-image-crop/dist/ReactCrop.css';
import './ImageModal.css';

const ImageModal = ({ image, isOpen, onClose, onSave }) => {
  const [activeTool, setActiveTool] = useState('select');
  const [brushColor, setBrushColor] = useState('#ff0000');
  const [brushWidth, setBrushWidth] = useState(5);
  const [cropMode, setCropMode] = useState(false);
  const [crop, setCrop] = useState({ unit: '%', width: 80, height: 80 });
  const [completedCrop, setCompletedCrop] = useState(null);
  const imgRef = useRef(null);

  const { editor, onReady } = useFabricJSEditor();

  useEffect(() => {
    if (editor && image && isOpen) {
      // Clear canvas and add image when modal opens
      editor.canvas.clear();
      fabric.Image.fromURL(image.file_path, (img) => {
        img.scaleToWidth(editor.canvas.width);
        editor.canvas.add(img);
        editor.canvas.renderAll();
      });
    }
  }, [editor, image, isOpen]);

  const handleToolChange = (tool) => {
    setActiveTool(tool);
    if (editor) {
      editor.canvas.isDrawingMode = tool === 'brush';
      editor.canvas.selection = tool === 'select';
    }
  };

  const handleAddText = () => {
    if (editor) {
      const text = new fabric.IText('Double click to edit', {
        left: 100,
        top: 100,
        fontFamily: 'Arial',
        fill: brushColor,
        fontSize: 20
      });
      editor.canvas.add(text);
      editor.canvas.setActiveObject(text);
    }
  };

  const handleSave = () => {
    if (editor) {
      const dataURL = editor.canvas.toDataURL({
        format: 'png',
        quality: 1
      });
      onSave(dataURL);
    }
  };

  const handleCropComplete = async () => {
    if (imgRef.current && completedCrop) {
      const croppedImage = await getCroppedImg(imgRef.current, completedCrop);
      fabric.Image.fromURL(croppedImage, (img) => {
        editor.canvas.clear();
        editor.canvas.add(img);
        editor.canvas.renderAll();
        setCropMode(false);
      });
    }
  };

  const getCroppedImg = (image, crop) => {
    const canvas = document.createElement('canvas');
    const ctx = canvas.getContext('2d');
    const scaleX = image.naturalWidth / image.width;
    const scaleY = image.naturalHeight / image.height;

    canvas.width = crop.width;
    canvas.height = crop.height;

    ctx.drawImage(
      image,
      crop.x * scaleX,
      crop.y * scaleY,
      crop.width * scaleX,
      crop.height * scaleY,
      0,
      0,
      crop.width,
      crop.height
    );

    return canvas.toDataURL('image/png');
  };

  return (
    <ReactModal
      isOpen={isOpen}
      onRequestClose={onClose}
      className="image-modal"
      overlayClassName="image-modal-overlay"
    >
      <div className="modal-header">
        <h3>Edit Image: {image?.title || image?.original_filename}</h3>
        <button onClick={onClose} className="close-btn">√ó</button>
      </div>

      <div className="modal-content">
        <div className="toolbar">
          <div className="tool-group">
            <button
              className={activeTool === 'select' ? 'active' : ''}
              onClick={() => handleToolChange('select')}
            >
              ‚úã Select
            </button>
            <button
              className={activeTool === 'brush' ? 'active' : ''}
              onClick={() => handleToolChange('brush')}
            >
              üñåÔ∏è Brush
            </button>
            <button onClick={handleAddText}>
              üìù Text
            </button>
            <button
              className={cropMode ? 'active' : ''}
              onClick={() => setCropMode(!cropMode)}
            >
              ‚úÇÔ∏è Crop
            </button>
          </div>

          {activeTool === 'brush' && (
            <div className="brush-controls">
              <input
                type="color"
                value={brushColor}
                onChange={(e) => {
                  setBrushColor(e.target.value);
                  if (editor) {
                    editor.canvas.freeDrawingBrush.color = e.target.value;
                  }
                }}
              />
              <input
                type="range"
                min="1"
                max="50"
                value={brushWidth}
                onChange={(e) => {
                  setBrushWidth(e.target.value);
                  if (editor) {
                    editor.canvas.freeDrawingBrush.width = parseInt(e.target.value);
                  }
                }}
              />
              <span>{brushWidth}px</span>
            </div>
          )}
        </div>

        <div className="canvas-container">
          {cropMode ? (
            <div className="crop-mode">
              <ReactCrop
                crop={crop}
                onChange={setCrop}
                onComplete={setCompletedCrop}
              >
                <img
                  ref={imgRef}
                  src={image?.file_path}
                  alt="Crop preview"
                  style={{ maxWidth: '100%' }}
                />
              </ReactCrop>
              <div className="crop-controls">
                <button onClick={handleCropComplete} className="btn btn-primary">
                  Apply Crop
                </button>
                <button onClick={() => setCropMode(false)} className="btn">
                  Cancel
                </button>
              </div>
            </div>
          ) : (
            <FabricJSCanvas className="fabric-canvas" onReady={onReady} />
          )}
        </div>
      </div>

      <div className="modal-footer">
        <button onClick={onClose} className="btn">
          Cancel
        </button>
        <button onClick={handleSave} className="btn btn-primary">
          Save Changes
        </button>
      </div>
    </ReactModal>
  );
};

export default ImageModal;
