import React, { useState, useRef, useEffect } from 'react';
import ReactModal from 'react-modal';
import ReactCrop from 'react-image-crop';
import 'react-image-crop/dist/ReactCrop.css';
import './ImageModal.css';

// Set the app element for react-modal
ReactModal.setAppElement('#root');

const ImageModal = ({ image, isOpen, onClose, onSave }) => {
  const [activeTool, setActiveTool] = useState('select');
  const [brushColor, setBrushColor] = useState('#ff0000');
  const [brushWidth, setBrushWidth] = useState(5);
  const [cropMode, setCropMode] = useState(false);
  const [crop, setCrop] = useState({ unit: '%', width: 80, height: 80 });
  const [completedCrop, setCompletedCrop] = useState(null);
  const [isDrawing, setIsDrawing] = useState(false);
  const [isImageLoaded, setIsImageLoaded] = useState(false);
  
  const canvasRef = useRef(null);
  const imgRef = useRef(null);
  const contextRef = useRef(null);
  const imageRef = useRef(null);

  useEffect(() => {
    if (isOpen && image) {
      setIsImageLoaded(false);
      const img = new Image();
      img.crossOrigin = 'anonymous';
      img.onload = () => {
        imageRef.current = img;
        setIsImageLoaded(true);
        
        // Initialize canvas only when image is loaded
        if (canvasRef.current) {
          const canvas = canvasRef.current;
          const ctx = canvas.getContext('2d');
          contextRef.current = ctx;
          
          canvas.width = img.width;
          canvas.height = img.height;
          ctx.drawImage(img, 0, 0);
        }
      };
      img.src = image.file_path;
    }
  }, [isOpen, image]);

  const startDrawing = (e) => {
    if (activeTool !== 'brush' || !contextRef.current) return;
    
    const canvas = canvasRef.current;
    const rect = canvas.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;
    
    contextRef.current.beginPath();
    contextRef.current.moveTo(x, y);
    setIsDrawing(true);
  };

  const draw = (e) => {
    if (!isDrawing || !contextRef.current) return;
    
    const canvas = canvasRef.current;
    const rect = canvas.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;
    
    contextRef.current.lineTo(x, y);
    contextRef.current.strokeStyle = brushColor;
    contextRef.current.lineWidth = brushWidth;
    contextRef.current.lineCap = 'round';
    contextRef.current.stroke();
  };

  const stopDrawing = () => {
    if (contextRef.current) {
      contextRef.current.closePath();
    }
    setIsDrawing(false);
  };

  const handleAddText = () => {
    if (!contextRef.current) return;
    
    const text = prompt('Enter text:');
    if (text) {
      contextRef.current.font = '20px Arial';
      contextRef.current.fillStyle = brushColor;
      contextRef.current.fillText(text, 100, 100);
    }
  };

  const handleSave = () => {
    if (!canvasRef.current) return;
    
    const dataURL = canvasRef.current.toDataURL('image/png');
    onSave(dataURL);
  };

  const handleCropComplete = async () => {
    if (!imgRef.current || !completedCrop || !contextRef.current) return;

    const croppedImage = await getCroppedImg(imgRef.current, completedCrop);
    const img = new Image();
    img.onload = () => {
      if (canvasRef.current && contextRef.current) {
        const canvas = canvasRef.current;
        const ctx = contextRef.current;
        canvas.width = img.width;
        canvas.height = img.height;
        ctx.drawImage(img, 0, 0);
        setCropMode(false);
      }
    };
    img.src = croppedImage;
  };

  const getCroppedImg = (image, crop) => {
    const canvas = document.createElement('canvas');
    const ctx = canvas.getContext('2d');
    const scaleX = image.naturalWidth / image.width;
    const scaleY = image.naturalHeight / image.height;

    canvas.width = crop.width;
    canvas.height = crop.height;

    ctx.drawImage(
      image,
      crop.x * scaleX,
      crop.y * scaleY,
      crop.width * scaleX,
      crop.height * scaleY,
      0,
      0,
      crop.width,
      crop.height
    );

    return canvas.toDataURL('image/png');
  };

  const clearCanvas = () => {
    if (!canvasRef.current || !contextRef.current || !imageRef.current) return;
    
    const canvas = canvasRef.current;
    const ctx = contextRef.current;
    ctx.clearRect(0, 0, canvas.width, canvas.height);
    
    // Reload original image
    ctx.drawImage(imageRef.current, 0, 0);
  };

  return (
    <ReactModal
      isOpen={isOpen}
      onRequestClose={onClose}
      className="image-modal"
      overlayClassName="image-modal-overlay"
    >
      <div className="modal-header">
        <h3>Edit Image: {image?.title || image?.original_filename}</h3>
        <button onClick={onClose} className="close-btn">√ó</button>
      </div>

      <div className="modal-content">
        <div className="toolbar">
          <div className="tool-group">
            <button
              className={activeTool === 'select' ? 'active' : ''}
              onClick={() => setActiveTool('select')}
            >
              ‚úã Select
            </button>
            <button
              className={activeTool === 'brush' ? 'active' : ''}
              onClick={() => setActiveTool('brush')}
            >
              üñåÔ∏è Brush
            </button>
            <button onClick={handleAddText}>
              üìù Text
            </button>
            <button
              className={cropMode ? 'active' : ''}
              onClick={() => setCropMode(!cropMode)}
            >
              ‚úÇÔ∏è Crop
            </button>
            <button onClick={clearCanvas}>
              üóëÔ∏è Clear
            </button>
          </div>

          {activeTool === 'brush' && (
            <div className="brush-controls">
              <input
                type="color"
                value={brushColor}
                onChange={(e) => setBrushColor(e.target.value)}
              />
              <input
                type="range"
                min="1"
                max="50"
                value={brushWidth}
                onChange={(e) => setBrushWidth(e.target.value)}
              />
              <span>{brushWidth}px</span>
            </div>
          )}
        </div>

        <div className="canvas-container">
          {!isImageLoaded ? (
            <div className="loading">Loading image...</div>
          ) : cropMode ? (
            <div className="crop-mode">
              <ReactCrop
                crop={crop}
                onChange={setCrop}
                onComplete={setCompletedCrop}
              >
                <img
                  ref={imgRef}
                  src={image?.file_path}
                  alt="Crop preview"
                  style={{ maxWidth: '100%' }}
                />
              </ReactCrop>
              <div className="crop-controls">
                <button onClick={handleCropComplete} className="btn btn-primary">
                  Apply Crop
                </button>
                <button onClick={() => setCropMode(false)} className="btn">
                  Cancel
                </button>
              </div>
            </div>
          ) : (
            <canvas
              ref={canvasRef}
              className="drawing-canvas"
              onMouseDown={startDrawing}
              onMouseMove={draw}
              onMouseUp={stopDrawing}
              onMouseLeave={stopDrawing}
              style={{ 
                cursor: activeTool === 'brush' ? 'crosshair' : 'default',
                maxWidth: '100%',
                maxHeight: '100%',
                border: '2px solid var(--bg-tertiary)',
                borderRadius: '8px'
              }}
            />
          )}
        </div>
      </div>

      <div className="modal-footer">
        <button onClick={onClose} className="btn">
          Cancel
        </button>
        <button onClick={handleSave} className="btn btn-primary">
          Save Changes
        </button>
      </div>
    </ReactModal>
  );
};

export default ImageModal;
